import React, { useEffect } from "react";
import * as d3 from "d3";

const BarChart = ({ data }) => {
  useEffect(() => {
    if (data) {
      drawBarChart(data);
    }
  }, [data]);

  const drawBarChart = (data) => {
    // Clear any existing chart
    d3.select("#bar-chart").selectAll("*").remove();

    // Set the dimensions for the chart
    const margin = { top: 20, right: 30, bottom: 40, left: 40 };
    const width = 800 - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    // Create SVG container
    const svg = d3
      .select("#bar-chart")
      .append("svg")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    // Set the scales
    const x = d3
      .scaleBand()
      .domain(data.map((d) => d.car_model))
      .range([0, width])
      .padding(0.1);

    const y = d3.scaleLinear().domain([0, d3.max(data, (d) => d.price)]).nice().range([height, 0]);

    // Add X axis
    svg
      .append("g")
      .selectAll(".x-axis")
      .data(data)
      .enter()
      .append("text")
      .attr("x", (d) => x(d.car_model) + x.bandwidth() / 2)
      .attr("y", height + 30)
      .style("text-anchor", "middle")
      .text((d) => d.car_model);

    // Add Y axis
    svg
      .append("g")
      .call(d3.axisLeft(y));

    // Create bars
    svg
      .selectAll(".bar")
      .data(data)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("x", (d) => x(d.car_model))
      .attr("y", (d) => y(d.price))
      .attr("width", x.bandwidth())
      .attr("height", (d) => height - y(d.price))
      .attr("fill", "steelblue");
  };

  return <div id="bar-chart"></div>;
};

export default BarChart;
