import React, { useEffect } from "react";
import * as d3 from "d3";

const LineChart = ({ data }) => {
  useEffect(() => {
    if (data.length === 0) return;

    // Clear previous chart
    d3.select("#line-chart").selectAll("*").remove();

    // Set chart dimensions
    const margin = { top: 20, right: 30, bottom: 50, left: 50 };
    const width = 800 - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    // Parse date and sort data by date
    const parseDate = d3.timeParse("%Y-%m-%d");
    const formattedData = data.map(d => ({
      date: parseDate(d.date_of_sale),
      price: d.price
    })).sort((a, b) => a.date - b.date);

    // Create scales
    const x = d3.scaleTime()
      .domain(d3.extent(formattedData, d => d.date))
      .range([0, width]);

    const y = d3.scaleLinear()
      .domain([0, d3.max(formattedData, d => d.price)])
      .nice()
      .range([height, 0]);

    // Create SVG
    const svg = d3.select("#line-chart")
      .append("svg")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`);

    // Create line generator
    const line = d3.line()
      .x(d => x(d.date))
      .y(d => y(d.price))
      .curve(d3.curveMonotoneX);

    // Append line path
    svg.append("path")
      .datum(formattedData)
      .attr("fill", "none")
      .attr("stroke", "steelblue")
      .attr("stroke-width", 2)
      .attr("d", line);

    // Add X-axis with formatted labels
    svg.append("g")
      .attr("transform", `translate(0, ${height})`)
      .call(d3.axisBottom(x).tickFormat(d3.timeFormat("%b %Y"))) // Format as "Jan 2024"
      .selectAll("text")
      .attr("transform", "rotate(-30)") // Rotate labels to prevent overlap
      .style("text-anchor", "end");

    // Add Y-axis
    svg.append("g")
      .call(d3.axisLeft(y));

    // Add X-axis label
    svg.append("text")
      .attr("transform", `translate(${width / 2}, ${height + margin.bottom - 10})`)
      .style("text-anchor", "middle")
      .text("Date of Sale");

    // Add Y-axis label
    svg.append("text")
      .attr("transform", "rotate(-90)")
      .attr("y", -margin.left + 10)
      .attr("x", -height / 2)
      .attr("dy", "1em")
      .style("text-anchor", "middle")
      .text("Price (USD)");

  }, [data]);

  return <div id="line-chart"></div>;
};

export default LineChart;
