import React, { useState, useEffect } from "react";
import axios from "axios";
import BarChart from "./BarChart";  // Import the BarChart component

function App() {
  const [tasks, setTasks] = useState({});
  const [taskData, setTaskData] = useState([]);
  const [taskId, setTaskId] = useState(null);

  useEffect(() => {
    // Fetching all tasks data
    axios.get("http://localhost:8000/tasks")
      .then((response) => {
        setTasks(response.data); // Set the list of tasks
      })
      .catch((error) => {
        console.error("Error fetching tasks:", error);
      });
  }, []);

  const handleTaskClick = (taskId) => {
    // Fetch data for the specific task when clicked
    axios.get(`http://localhost:8000/tasks/${taskId}`)
      .then((response) => {
        setTaskData(response.data.sales_data); // Assuming the backend sends sales data
        setTaskId(taskId); // Update taskId for visualization
      })
      .catch((error) => {
        console.error("Error fetching task data:", error);
      });
  };

  return (
    <div>
      <h1>Task List</h1>
      {Object.keys(tasks).length === 0 ? (
        <p>No tasks found</p>
      ) : (
        <ul>
          {Object.entries(tasks).map(([taskId, taskStatus]) => (
            <li key={taskId}>
              Task {taskId}: {taskStatus}
              <button onClick={() => handleTaskClick(taskId)}>View Analytics</button>
            </li>
          ))}
        </ul>
      )}

      {taskId && taskData.length > 0 && (
        <div>
          <h2>Task {taskId} Analytics</h2>
          <BarChart data={taskData} /> {/* Pass sales data to BarChart */}
        </div>
      )}
    </div>
  );
}

export default App;
