import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [tasks, setTasks] = useState({});

  useEffect(() => {
    axios.get("http://localhost:8000/tasks")
      .then((response) => {
        setTasks(response.data); // Directly set the object
      })
      .catch((error) => {
        console.error("Error fetching tasks:", error);
      });
  }, []);

  return (
    <div>
      <h1>Task List</h1>
      {Object.keys(tasks).length === 0 ? (
        <p>No tasks found</p>
      ) : (
        <ul>
          {Object.entries(tasks).map(([taskId, taskStatus]) => (
            <li key={taskId}>Task {taskId}: {taskStatus}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
