import React, { useState, useEffect } from "react";
import axios from "axios";
import BarChart from "./BarChart";
import LineChart from "./LineChart"; // Import the LineChart component

function App() {
  const [tasks, setTasks] = useState({});
  const [selectedTaskData, setSelectedTaskData] = useState(null);
  const [selectedYear, setSelectedYear] = useState("2024"); // Default year

  useEffect(() => {
    axios.get("http://localhost:8000/tasks")
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error("Error fetching tasks:", error);
      });
  }, []);

  // Fetch task data for analytics when a "View Analytics" button is clicked
  const handleViewAnalytics = (taskId) => {
    axios.get(`http://localhost:8000/tasks/${taskId}`)
      .then((response) => {
        setSelectedTaskData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching task data:", error);
      });
  };

  // Filter the task data based on the selected year
  const filteredData = selectedTaskData
    ? selectedTaskData.filter(d => d.date_of_sale.startsWith(selectedYear))
    : [];

  return (
    <div>
      <h1>Task List</h1>
      {Object.keys(tasks).length === 0 ? (
        <p>No tasks found</p>
      ) : (
        <ul>
          {Object.entries(tasks).map(([taskId, taskStatus]) => (
            <li key={taskId}>
              Task {taskId}: {taskStatus}
              <button onClick={() => handleViewAnalytics(taskId)}>View Analytics</button>
            </li>
          ))}
        </ul>
      )}

      {selectedTaskData && (
        <div>
          <h2>Analytics for Task {selectedTaskData.task_id}</h2>

          {/* Dropdown for year selection */}
          <label>Select Year: </label>
          <select onChange={(e) => setSelectedYear(e.target.value)} value={selectedYear}>
            <option value="2023">2023</option>
            <option value="2024">2024</option>
          </select>

          {/* Pass only filtered data */}
          <BarChart data={filteredData} />
          <LineChart data={filteredData} />
        </div>
      )}
    </div>
  );
}

export default App;
