import React, { useState, useEffect } from "react";
import axios from "axios";
import BarChart from "./BarChart";
import LineChart from "./LineChart";
import PieChart from "./PieChart"; // ✅ Import Pie Chart
import "./App.css"; // Ensure styles are applied

function App() {
  const [tasks, setTasks] = useState({});
  const [selectedTaskData, setSelectedTaskData] = useState(null);
  const [selectedYear, setSelectedYear] = useState("2024");
  const [selectedBrand, setSelectedBrand] = useState("All");
  const [loading, setLoading] = useState(false);
  const [creatingTask, setCreatingTask] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const tasksPerPage = 5;

  // Task Creation Form State
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [selectedBrands, setSelectedBrands] = useState([]);
  const availableBrands = ["Toyota", "Honda", "Ford"];

  useEffect(() => {
    fetchTasks();
    const interval = setInterval(fetchTasks, 10000); // Auto-refresh every 10 sec
    return () => clearInterval(interval); // Cleanup interval
  }, []);

  const fetchTasks = () => {
    setLoading(true);
    axios.get("http://localhost:8000/tasks")
      .then((response) => {
        setTasks(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching tasks:", error);
        setLoading(false);
      });
  };

  const handleViewAnalytics = (taskId) => {
    setLoading(true);
    axios.get(`http://localhost:8000/tasks/${taskId}`)
      .then((response) => {
        setSelectedTaskData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching task data:", error);
        setLoading(false);
      });
  };

  const handleCreateTask = (e) => {
    e.preventDefault();

    if (!startDate || !endDate || selectedBrands.length === 0) {
      alert("Please fill all fields.");
      return;
    }

    setCreatingTask(true);
    axios.post("http://localhost:8000/tasks", {
      start_date: startDate,
      end_date: endDate,
      brands: selectedBrands
    })
      .then((response) => {
        alert(`✅ Task Created! Task ID: ${response.data.task_id}`);
        setTasks((prevTasks) => ({
          ...prevTasks,
          [response.data.task_id]: response.data.status
        }));
        setStartDate("");
        setEndDate("");
        setSelectedBrands([]);
      })
      .catch((error) => {
        console.error("Error creating task:", error);
      })
      .finally(() => {
        setCreatingTask(false);
      });
  };

  const filteredData = selectedTaskData
    ? selectedTaskData.filter(d =>
        d.date_of_sale.startsWith(selectedYear) &&
        (selectedBrand === "All" || d.company === selectedBrand)
      )
    : [];

  const uniqueBrands = selectedTaskData
    ? ["All", ...new Set(selectedTaskData.map(d => d.company))]
    : ["All"];

  // Pagination logic
  const taskEntries = Object.entries(tasks);
  const totalPages = Math.ceil(taskEntries.length / tasksPerPage);
  const paginatedTasks = taskEntries.slice((currentPage - 1) * tasksPerPage, currentPage * tasksPerPage);

  return (
    <div className="app-container">
      <h1 className="title">Task List</h1>

      {/* Task Creation Form */}
      <form onSubmit={handleCreateTask} className="task-form">
        <label>Start Date:</label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />

        <label>End Date:</label>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} required />

        <label>Brands:</label>
        <select multiple value={selectedBrands} onChange={(e) => setSelectedBrands([...e.target.selectedOptions].map(o => o.value))} required>
          {availableBrands.map((brand) => (
            <option key={brand} value={brand}>{brand}</option>
          ))}
        </select>

        <button type="submit" disabled={creatingTask}>
          {creatingTask ? "Creating..." : "Create Task"}
        </button>
      </form>

      {/* Loading Indicator */}
      {loading && <p className="loading">Fetching data...</p>}

      {/* Task List with Pagination */}
      {taskEntries.length === 0 && !loading ? (
        <p>No tasks found</p>
      ) : (
        <>
          <ul className="task-list">
            {paginatedTasks.map(([taskId, taskStatus]) => (
              <li key={taskId}>
                Task {taskId}: {taskStatus}
                <button onClick={() => handleViewAnalytics(taskId)}>View Analytics</button>
              </li>
            ))}
          </ul>
          {/* Pagination Controls */}
          <div className="pagination">
            <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>← Prev</button>
            <span>Page {currentPage} of {totalPages}</span>
            <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === totalPages}>Next →</button>
          </div>
        </>
      )}

      {/* Analytics Section */}
      {selectedTaskData && (
        <div>
          <h2 className="analytics-title">Analytics for Task</h2>

          {/* Filters */}
          <div className="filters">
            <label>Select Year:</label>
            <select onChange={(e) => setSelectedYear(e.target.value)} value={selectedYear}>
              <option value="2023">2023</option>
              <option value="2024">2024</option>
            </select>

            <label>Select Brand:</label>
            <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
              {uniqueBrands.map((brand) => (
                <option key={brand} value={brand}>
                  {brand}
                </option>
              ))}
            </select>
          </div>

          {/* Charts */}
          <div className="chart-container">
            <div className="chart">
              <BarChart data={filteredData} />
            </div>
            <div className="chart">
              <LineChart data={filteredData} />
            </div>
            <div className="chart">
              <PieChart data={filteredData} /> {/* ✅ Added Pie Chart */}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
