import React, { useState, useEffect } from "react";
import axios from "axios";
import BarChart from "./BarChart";
import LineChart from "./LineChart";
import "./App.css";  // Import CSS for better styling

function App() {
  const [tasks, setTasks] = useState({});
  const [selectedTaskData, setSelectedTaskData] = useState(null);
  const [selectedYear, setSelectedYear] = useState("2024"); // Default year
  const [selectedBrand, setSelectedBrand] = useState("All"); // Default brand

  useEffect(() => {
    axios.get("http://localhost:8000/tasks")
      .then((response) => setTasks(response.data))
      .catch((error) => console.error("Error fetching tasks:", error));
  }, []);

  // Fetch task data for analytics when a "View Analytics" button is clicked
  const handleViewAnalytics = (taskId) => {
    axios.get(`http://localhost:8000/tasks/${taskId}`)
      .then((response) => setSelectedTaskData(response.data))
      .catch((error) => console.error("Error fetching task data:", error));
  };

  // Filter the task data based on selected year and brand
  const filteredData = selectedTaskData
    ? selectedTaskData.filter(d => 
        d.date_of_sale.startsWith(selectedYear) && 
        (selectedBrand === "All" || d.company === selectedBrand)
      )
    : [];

  // Extract unique brand names from the task data
  const uniqueBrands = selectedTaskData 
    ? ["All", ...new Set(selectedTaskData.map(d => d.company))] 
    : ["All"];

  return (
    <div className="app-container">
      <h1>Task List</h1>
      {Object.keys(tasks).length === 0 ? (
        <p>No tasks found</p>
      ) : (
        <ul className="task-list">
          {Object.entries(tasks).map(([taskId, taskStatus]) => (
            <li key={taskId}>
              Task {taskId}: {taskStatus}
              <button onClick={() => handleViewAnalytics(taskId)}>View Analytics</button>
            </li>
          ))}
        </ul>
      )}

      {selectedTaskData && (
        <div className="chart-container">
          <h2>Analytics for Task</h2>

          {/* Filters for Year & Brand */}
          <div className="filters">
            <label>Select Year: </label>
            <select onChange={(e) => setSelectedYear(e.target.value)} value={selectedYear}>
              <option value="2023">2023</option>
              <option value="2024">2024</option>
            </select>

            <label>Select Brand: </label>
            <select onChange={(e) => setSelectedBrand(e.target.value)} value={selectedBrand}>
              {uniqueBrands.map((brand) => (
                <option key={brand} value={brand}>{brand}</option>
              ))}
            </select>
          </div>

          {/* Charts */}
          <div className="chart">
            <BarChart data={filteredData} />
          </div>
          <div className="chart">
            <LineChart data={filteredData} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
