import React, { useEffect } from "react";
import * as d3 from "d3";

function BarChart({ data }) {
  useEffect(() => {
    const svg = d3.select("#barChart")
      .attr("width", 500)
      .attr("height", 300);

    const x = d3.scaleBand()
      .domain(data.map(d => d.brand))  // Assuming data contains 'brand' and 'sales'
      .range([0, 500])
      .padding(0.1);

    const y = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.sales)])  // Sales as the measure
      .nice()
      .range([300, 0]);

    svg.selectAll(".bar")
      .data(data)
      .enter()
      .append("rect")
      .attr("class", "bar")
      .attr("x", d => x(d.brand))
      .attr("y", d => y(d.sales))
      .attr("width", x.bandwidth())
      .attr("height", d => 300 - y(d.sales))
      .attr("fill", "steelblue");

    svg.append("g")
      .selectAll(".x-axis")
      .data([0])
      .enter()
      .append("g")
      .attr("transform", "translate(0,300)")
      .call(d3.axisBottom(x));

    svg.append("g")
      .selectAll(".y-axis")
      .data([0])
      .enter()
      .append("g")
      .call(d3.axisLeft(y));
  }, [data]);

  return <svg id="barChart"></svg>;
}

export default BarChart;
